package com.example.mapsandroid;

import androidx.appcompat.app.AppCompatActivity;

import android.os.Bundle;
import android.util.DisplayMetrics;
import android.widget.ImageView;
import android.widget.TextView;

import com.bumptech.glide.Glide;

import java.time.Instant;

public class PopupImgActivity extends AppCompatActivity {
ImageView img1;
TextView txtDescrip;


    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_popup_img);
        
        
        DisplayMetrics medidasVentana = new DisplayMetrics();
        getWindowManager().getDefaultDisplay().getRealMetrics(medidasVentana);

        int ancho = medidasVentana.widthPixels;
        int alto = medidasVentana.heightPixels;

        getWindow().setLayout((int)(ancho*0.85),(int)(alto*0.5));

        img1 = findViewById(R.id.img1);
        txtDescrip = findViewById(R.id.txt_descripcion);
        
        Bundle bundle = this.getIntent().getExtras();

        if (bundle.getString("Click").equals("Alcaldia Municipal")){
            txtDescrip.setText("Nombre del lugar: "+bundle.getString("Click"));
            String url = "data:image/jpeg;base64,";
            txtDescrip.setText("Primera Calle Oriente, Barrio El Calvario, San José Guayabal, Departamento de Cuscatlán");
            Glide.with(this).load(url).into(img1);
           
        }
        if (bundle.getString("Click").equals("Parque")){
            txtDescrip.setText("Nombre del lugar: "+bundle.getString("Click"));
            txtDescrip.setText("1a Avenida Sur, San José Guayabal");
            String url = "https://pbs.twimg.com/media/EogsLQzXMAAzmM7.jpg";
            Glide.with(this).load(url).into(img1);

        }
        if (bundle.getString("Click").equals("Iglesia San Josè Guayabal")){
            txtDescrip.setText("Nombre del lugar: "+bundle.getString("Click"));
            String url = "https://upload.wikimedia.org/wikipedia/commons/7/73/Iglesia_Parroquial_de_San_Jos%C3%A9_Guayabal.JPG";
            txtDescrip.setText("1ª. Avenida Sur, Barrio La Virgen, San José Guayabal");
            Glide.with(this).load(url).into(img1);

        }
        if (bundle.getString("Click").equals("Cementerio Municipal")){
            txtDescrip.setText("Nombre del lugar: "+bundle.getString("Click"));
            String url = "https://cdn-pro.elsalvador.com/wp-content/uploads/2019/04/entierro21.jpg";
            txtDescrip.setText("");
            Glide.with(this).load(url).into(img1);

        }
        if (bundle.getString("Click").equals("Mercado Municipal")){
            txtDescrip.setText("Nombre del lugar: "+bundle.getString("Click"));
            String url = "data:image/jpeg;base64,";
            txtDescrip.setText("1a Avenida Sur, San José Guayabal");
            Glide.with(this).load(url).into(img1);

        }
    }
}